import React, { useEffect, useState } from 'react';
import Cards from './components/card';
import logo from './logo.svg';
import './App.css';

function App() {

  function generatenums () {
    let numarr = [];
    while(numarr.length<9) {
      let randnum = Math.floor(Math.random() * 30)
      if (!numarr.includes(randnum)) {
      numarr.push(randnum)
    }
    }
    return numarr;
  }

  const [randomarr, setRandomArr] = useState(() => {
    const initialarr = generatenums();
    return initialarr;
  });
  const [chosencards, setChosenCards] = useState(['none']);
  const [lastchosen, setLastChosen] = useState('');
  const [score, setScore] = useState(0);
  const [highscore, setHighScore] = useState(0);

  useEffect(() => {
      if (chosencards[0] === 'none') {
      let chosencopy = [];
      chosencopy.push(lastchosen);
      setChosenCards(chosencopy);
      setScore(s => s + 1)
      }
      if (chosencards[0] !== 'none') {
        if (!chosencards.includes(lastchosen)) {
          let chosencopy = [...chosencards];
          chosencopy.push(lastchosen);
          setChosenCards(chosencopy);
          setScore(s => s + 1)
        }
        if (chosencards.includes(lastchosen)) {
          setChosenCards(['none']);  
          setScore(0);
        }
        }
  }, [lastchosen, chosencards])

  return (
    <div className="App">
    <div id="gametitle">NBA Memory Card Game</div>
    <div id="scorecont">
      <div id="score">Score: {score}</div>
      <div id="highscore">High Score: {highscore} </div>
    </div>
    <ul className="cardlist">
      {randomarr.map((rand) => {
        return(
          <li key={rand}>
            <Cards
            value={rand}
            onClick={(cardclicked) => {
              console.log(cardclicked);
              setLastChosen(cardclicked);
              setRandomArr(() => {
                const newarr = generatenums();
                return newarr;
                });
            }}
            />
          </li>
        )
      })}
    </ul>
    </div>
  );
}

export default App;
