import React, { useState } from 'react';
import Cards from './components/card';
import logo from './logo.svg';
import './App.css';

function App() {

  function generatenums () {
    let numarr = [];
    while(numarr.length<9) {
      let randnum = Math.floor(Math.random() * 30)
      if (!numarr.includes(randnum)) {
      numarr.push(randnum)
    }
    }
    return numarr;
  }

  const [randomarr, setRandomArr] = useState(generatenums());
  const [score, setScore] = useState(0);
  const [highscore, setHighScore] = useState(0);
  const [chosencards, setChosenCards] = useState([]);

  function HandleCardClick(cardclicked) {
      if (!chosencards.includes(cardclicked)) {
        setScore(score + 1)
        let chosencopy= [...chosencards];
        chosencopy.push(cardclicked);
        setChosenCards(chosencopy);
        setRandomArr(generatenums());
        return;
      }
      if (chosencards.includes(cardclicked)) {
        if (score > highscore) {
          setHighScore(score);
        }
        setScore(0);
        setChosenCards('');
        setRandomArr(generatenums());
        return;
      }
    }

  return (
    <div className="App">
    <div id="gametitle">NBA Memory Card Game</div>
    <div id="scorecont">
      <div id="score">Score: {score}</div>
      <div id="highscore">High Score: {highscore} </div>
    </div>
    <ul class="cardlist">
      {randomarr.map((rand) => {
        return(
          <li key={rand}>
            <Cards
            value={rand}
            onClick={HandleCardClick}
            />
          </li>
        )
      })}
    </ul>
    </div>
  );
}

export default App;
