import React, { useEffect, useState } from 'react';
import Cards from './components/card';
import logo from './logo.svg';
import './App.css';

function App() {

  function generatenums () {
    let numarr = [];
    while(numarr.length<9) {
      let randnum = Math.floor(Math.random() * 30)
      if (!numarr.includes(randnum)) {
      numarr.push(randnum)
    }
    }
    return numarr;
  }

  let initialarr = generatenums();


  const [randomarr, setRandomArr] = useState(initialarr);
  const [chosencards, setChosenCards] = useState(['none']);
  const [lastchosen, setLastChosen] = useState('');
  useEffect(function lookchosen() {
    if (chosencards[0] === 'none') {
      let chosencopy = [];
      chosencopy.push(lastchosen);
      setChosenCards(chosencopy);
    }
    if (chosencards[0] !== 'none') {
      if (!chosencards.includes(lastchosen)) {
        let chosencopy = [...chosencards];
        chosencopy.push(lastchosen);
        setChosenCards(chosencopy);
      }
      if (chosencards.includes(lastchosen)) {
        setChosenCards(['none']);
      }
    }
  },[chosencards, lastchosen]);
  const [score, setScore] = useState(0);
  useEffect(function lookscore() {
    if (chosencards.includes(lastchosen)) {
      setScore(0);
    }
    if (!chosencards.includes(lastchosen) && chosencards[0] !== 'none') {
      setScore(score +1);
    }
  },[chosencards, lastchosen, score])

  const [highscore, setHighScore] = useState(0);
  useEffect(function lookhighscore() {
    if (score > highscore) {
      setHighScore(score);
    }
  },[score,highscore])



  function HandleCardClick(cardclicked) {
        let newarr = generatenums()
        setRandomArr(newarr);
        setLastChosen(cardclicked)
        };

  return (
    <div className="App">
    <div id="gametitle">NBA Memory Card Game</div>
    <div id="scorecont">
      <div id="score">Score: {score}</div>
      <div id="highscore">High Score: {highscore} </div>
    </div>
    <ul className="cardlist">
      {randomarr.map((rand) => {
        return(
          <li key={rand}>
            <Cards
            value={rand}
            onClick={HandleCardClick}
            />
          </li>
        )
      })}
    </ul>
    </div>
  );
}

export default App;
